mapper.select:
SELECT OPER_HOUR,MAX_COST,MIN_COST,AVG_COST
FROM
SEARCH_COST_HOUR
WHERE
OPER_DATE = #{OPER_DATE}

AND
USER_ID=#{USER_ID}
AND
APP_ID=#{APP_ID}

order by OPER_HOUR asc

mapper.delete:
DELETE
FROM SEARCH_COST_DAY
WHERE
USER_ID=#{USER_ID}
AND
APP_ID=#{APP_ID}

DELETE
FROM SEARCH_COST_HOUR
WHERE
USER_ID=#{USER_ID}
AND
APP_ID=#{APP_ID}
